import { useEffect, useState } from "react";
import axios from "axios";

// type AxiosResponse = object[] | null;

const useGitProjectCalculator = () => {
  const [gitLangFreq, setGitLangFreq] = useState<Object>(null);
  const [gitLangs, setGitLangs] = useState<string[]>(null);
  const [isLoading, setLoading] = useState<boolean>(false);

  useEffect(() => {
    axios
      .get("https://api.github.com/users/craigwh10/repos")
      .then(function (response) {
        setGitLangFreq(
          response.data.reduce((acc, it) => {
            acc[it.language] = acc[it.language] + 1 || 1;
            return acc;
          }, {})
        );
        setGitLangs(Object.keys(response.data));
        console.log(Object.keys(gitLangFreq));
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  return { gitLangFreq, gitLangs, isLoading };
};

export default useGitProjectCalculator;
