import { useEffect, useState } from "react";
import axios from "axios";

// type AxiosResponse = object[] | null;

interface Content {
  language: number;
}

interface Frequency {
  data: Content;
}

interface Langs {
  data: Lang;
}

interface Lang {
  language: string;
}

const useGitProjectCalculator = () => {
  const [gitLangFreq, setGitRepos] = useState<Frequency>(null);
  const [gitLangs, setGitLangs] = useState<Langs>(null);
  const [isLoading, setLoading] = useState<boolean>(false);

  useEffect(() => {
    axios
      .get("https://api.github.com/users/craigwh10/repos")
      .then(function (response) {
        setGitRepos(
          response.data.reduce((acc, it) => {
            acc[it.language] = acc[it.language] + 1 || 1;
            return acc;
          }, {})
        );
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  return { gitLangFreq, isLoading };
};

export default useGitProjectCalculator;
