import { useEffect, useState } from "react";
import axios from "axios";

// type AxiosResponse = object[] | null;

// interface GitProject {
//   name: string;
// }

interface Frequency {
  language: number;
}

const useGitProjectCalculator = () => {
  const [gitLangFreq, setGitRepos] = useState < object < Frequency >> null;
  const [isLoading, setLoading] = useState<boolean>(false);

  useEffect(() => {
    axios
      .get("https://api.github.com/users/craigwh10/repos")
      .then(function (response) {
        setGitRepos(
          response.data.reduce((acc, it) => {
            acc[it.language] = acc[it.language] + 1 || 1;
            return acc;
          }, {})
        );
        setLoading(false);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  return { gitLangFreq, isLoading };
};

export default useGitProjectCalculator;
