import { useEffect, useState } from "react";
import axios from "axios";

// type AxiosResponse = object[] | null;

interface GitProject {
  data: object;
}

interface GitProjects {
  data: GitProject[];
}

const useGitProjects = () => {
  const [gitRepos, setGitRepos] = useState<Array<GitProjects>>(null);
  const [isLoading, setLoading] = useState<boolean>(false);

  useEffect(() => {
    const fetchGit = async () => {
      return await axios("https://api.github.com/users/craigwh10/repos");
    };
    let result = fetchGit();
    console.log(result);
    if (result) {
      setGitRepos(result.data);
      setLoading(false);
    }
  }, []);

  return [gitRepos, isLoading];
};

export default useGitProjects;
